function toggleCollapse(e){1==parseInt(e.getAttribute("data-collapsed"))?(e.innerText="(Collapse)",e.parentElement.parentElement.classList.remove("collapsed"),e.setAttribute("data-collapsed","0")):(e.innerText="(Expand)",e.parentElement.parentElement.classList.add("collapsed"),e.setAttribute("data-collapsed","1"))}document.addEventListener("DOMContentLoaded",function(){for(var e=document.querySelectorAll("a.collapse"),t=0;t<e.length;++t)e[t].setAttribute("data-collapsed","0"),e[t].addEventListener("click",toggleCollapse.bind(null,e[t]))});
